import React, { Component} from "react";
import './About.css';
import MyPic from './middle_my_pic3.jpg';
import instagram from './black_instagram.png';
import twitter from './black_twitter.png';
import linkedin from './black_linkedin.png';
import mail from './black_email.png';

class About extends Component {
    render() {
        return(
            <div className="Main-About">
                <div className="pinkDiv"></div>

                <div className="contentDiv">
                    <div className="picDiv">
                        <img src={MyPic} alt="MY_pIc_hErE" className="my_about_pic"/>
                        <h2 className="myNameHead" style={{marginTop : "7%"}}>Sahil</h2>
                        <h2 className="myNameHead" style={{marginTop : "2%",}}>Chopra</h2>

                        <div style={{borderTop : '2px solid royalblue', margin : '8% 43% 0 43%',}}></div>
                        <p style={{fontFamily : "'Space Mono', monospace", marginTop : "9%",}}>WEBSITE DEVELOPER</p>

                        <div className="linkDiv">
                        <img src={mail} alt="email_icon" width="7.5%" style={{margin : "0 4% 0 4%"}}/>
                        <img src={linkedin} alt="linkedin_icon" width="7.5%" style={{margin : "0 4% 0 4%"}}/>
                        <img src={twitter} alt="twitter_icon" width="7.5%" style={{margin : "0 4% 0 4%"}}/>
                        <img src={instagram} alt="insatgram_icon" width="7.5%" style={{margin : "0 4% 0 4%"}}/></div>
                    </div>




                    <div className="shortAbout">
                        <h1 className="shortHead">Hello</h1>
                        <h3 className="shortH3">Here's who I am and what I do</h3>
                        <div className="buttonDiv">
                        <button className={[ "btn", "resumebtn"].join(' ')}>RESUME</button>
                        <button className={[ "btn", "projectsbtn"].join(' ')}>PROJECTS</button>
                        </div>
                        <div style={{textAlign : "left", margin : "7% 7% 3% 7%"}}>
                        <h4 style={{marginBottom : "5%",}}>The Short :</h4>
                        <h5>I amd happy you stopped by ! See what i can do for you. Sahil Chopra is a Web developer with HTML, CSS, Javascript and React JS. He is a 3rd year Computer Science Engineering student who is really very interested in learning and exploring new things.</h5>
                        </div>
                    </div>







                    <div className="longAbout">
hey sahil 3
                    </div>
                </div>
            </div>
        );
    }
} 

export default About;
